package JavaBasics;

public class loops {

	public static void main(String[] args) {
		int i;
		for(i=0;i<5;i++)
		{
			System.out.println(i);
		}
		System.out.println("\n");

		
		int j=0;
		while(j<5)
		{
			System.out.println(j);
			j++;
		}
		System.out.println("\n");

		int k=0;
		do {
			System.out.println(k);
			k++;
		}while(k<5);
		// TODO Auto-generated method stub

	}

}
